mapper.select:

	select 
		 	t_no as tno,
            t_name as tname,
            t_addr as taddr,
            t_info as tinfo,
            t_photo as tphoto       
            from teacher_g4 where 
                                t_no not in
            (select booking_g4.t_no as tno from booking_g4	
             where booking_g4.bo_addr = #{boAddr}
             and bo_date = #{boDate}
             and bo_time in (#{boTime})
             ) and t_addr= #{boAddr}

select count(*) 
from parents_g4
where 
	p_name = #{pname}
and p_phone=#{pphone}

	select p_name as pname 
	      ,p_phone as pphone 
    from parents_g4
	where 
		p_name = #{pname}
	and p_phone=#{pphone}

select count(*)
from parents_g4 where 
p_name=#{pname} and p_phone=#{pphone}
